import React, { useState } from 'react';
import { db } from '../firebase/config';
import { collection, addDoc } from 'firebase/firestore';

function SupportPage() {
  const [name, setName] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  // SupportPage.js handleSubmit
const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    await addDoc(collection(db, "support_messages"), {
      name,
      subject,
      message,
      timestamp: new Date(),
      userId: 'guest',
      isAdmin: false,
      parentMessageId: null // Ana mesajlar için null
    });
    setMessage('');
    alert('Message sent successfully!');
  } catch (error) {
    alert('Error sending message');
  }
};


  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold mb-4">Customer Support</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Your Name"
            className="w-full p-2 border rounded"
            required
          />
          <select
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Issue</option>
            <option value="Defected Product">Defected Product</option>
            <option value="Late Order">Late Order</option>
            <option value="Lost Product">Lost Product</option>
            <option value="Suggestion">Suggestion</option>
          </select>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message..."
            className="w-full p-2 border rounded h-32"
            required
          />
          <button 
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Send Message
          </button>
        </form>
      </div>
    </div>
  );
}

export default SupportPage;
