import React, { useState, useEffect } from 'react';
import { db } from '../firebase/config';
import { collection, query, orderBy, onSnapshot, addDoc } from 'firebase/firestore';

function AdminPage() {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const q = query(collection(db, 'support_messages'), orderBy('timestamp', 'desc'));
    
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const messagesList = [];
      querySnapshot.forEach((doc) => {
        messagesList.push({ ...doc.data(), id: doc.id });
      });
      setMessages(messagesList);
    });

    return () => unsubscribe();
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>
      <div className="bg-white shadow rounded-lg p-4">
        {messages.map((msg) => (
          <div key={msg.id} className="border-b p-4">
            <div className="flex justify-between">
              <span className="font-bold">{msg.name}</span>
              <span className="text-gray-500">{new Date(msg.timestamp.toDate()).toLocaleString()}</span>
            </div>
            <div className="text-gray-600">{msg.subject}</div>
            <div className="mt-2">{msg.message}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminPage;
